plugin.install:
global $reefless, $rlDb;
$rlDb -> query("DROP TABLE IF EXISTS `". RL_DBPREFIX ."banner_plans`"); $rlDb -> query(" CREATE TABLE `". RL_DBPREFIX ."banner_plans` ( `ID` int(11) NOT NULL AUTO_INCREMENT, `Key` varchar(255) NOT NULL, `Position` int(5) NOT NULL DEFAULT '0', `Admin` enum('0','1') NOT NULL DEFAULT '0', `Allow_for` mediumtext NOT NULL, `Geo` enum('0','1','2') NOT NULL DEFAULT '0', `Country` mediumtext NOT NULL, `Boxes` mediumtext NOT NULL, `Types` mediumtext NOT NULL, `Color` varchar(7) NOT NULL, `Price` varchar(10) NOT NULL DEFAULT '0', `Plan_Type` enum('period','views') NOT NULL DEFAULT 'period', `Period` int(11) NOT NULL DEFAULT '0', `Status` enum('active','approval','trash') NOT NULL DEFAULT 'active', PRIMARY KEY (`ID`), KEY `Position` (`Position`) ) DEFAULT CHARSET=utf8;" );
$rlDb -> query("DROP TABLE IF EXISTS `". RL_DBPREFIX ."banners`"); $rlDb -> query(" CREATE TABLE `". RL_DBPREFIX ."banners` ( `ID` int(11) NOT NULL AUTO_INCREMENT, `Plan_ID` int(3) DEFAULT '0', `Account_ID` int(11) DEFAULT '0', `Box` varchar(255) NOT NULL, `Date_release` int(10) NOT NULL DEFAULT '0', `Date_from` int(10) NOT NULL DEFAULT '0', `Date_to` int(10) NOT NULL DEFAULT '0', `Pay_date` int(10) NOT NULL, `Shows` int(11) NOT NULL DEFAULT '0', `Last_show` int(10) NOT NULL DEFAULT '0', `Link` varchar(255) NOT NULL, `Type` enum('image','flash','html') NOT NULL DEFAULT 'image', `Follow` enum('0','1') NOT NULL DEFAULT '0', `Html` mediumtext NOT NULL, `Image` varchar(100) NOT NULL, `Last_step` varchar(15) NOT NULL, `Status` enum('active','approval','pending','incomplete','expired') NOT NULL DEFAULT 'approval', `Cron` enum('0','1') NOT NULL DEFAULT '0', `Cron_notified` enum('0','1') NOT NULL DEFAULT '0', PRIMARY KEY (`ID`), KEY `Status` (`Status`), KEY `Account_ID` (`Account_ID`), KEY `Plan_ID` (`Plan_ID`) ) DEFAULT CHARSET=utf8;" );
$rlDb -> query("DROP TABLE IF EXISTS `". RL_DBPREFIX ."banners_click`"); $rlDb -> query(" CREATE TABLE `". RL_DBPREFIX ."banners_click` ( `ID` int(11) NOT NULL AUTO_INCREMENT, `Banner_ID` int(11) NOT NULL, `Hash` varchar(32) NOT NULL, `Country` varchar(3) NOT NULL, PRIMARY KEY (`ID`), KEY `Hash` (`Hash`) ) DEFAULT CHARSET=utf8;" );
$rlDb -> query("ALTER TABLE `". RL_DBPREFIX ."blocks` ADD `Banners` MEDIUMTEXT NOT NULL"); $rlDb -> query("UPDATE `". RL_DBPREFIX ."lang_keys` SET `Module` = 'common' WHERE `Key` IN ('left','right','top','bottom','middle_right','middle_left')");
// create folder for banners $reefless -> rlMkdir(RL_FILES .'banners');

hooks.hook:

global $rlDb, $field, $value, $id;

if ( $_GET['controller'] == 'banners' && $field == 'Status' )
{
	$key = $rlDb -> getOne('Key', "`ID` = '{$id}'", 'blocks');
	$sql = "UPDATE `". RL_DBPREFIX ."hooks` SET `Status` = '{$value}' WHERE `Name` = 'tplBetweenCategories' AND `Plugin` = 'banners_{$key}' LIMIT 1";
	$rlDb -> query($sql);
}


global $data, $key, $config;

if ( $_GET['controller'] == 'banners' )
{
	if ( version_compare($config['rl_version'], '4.0.1', '>') )
	{
		foreach($data as $key => $box)
		{
			$boxInfo = unserialize($data[$key]['Banners']);
			$data[$key]['banners_size'] = "{$boxInfo['width']} x {$boxInfo['height']}";
			$data[$key]['banners_limit'] = $boxInfo['limit'];
		}
	}
	else
	{
		$boxInfo = unserialize($data[$key]['Banners']);
		$data[$key]['banners_size'] = "{$boxInfo['width']} x {$boxInfo['height']}";
		$data[$key]['banners_limit'] = $boxInfo['limit'];
	}
}


global $sql;

$replace[0] = ", `BT1`.`Key` AS `Banner_plan` FROM ";
$replace[1] = "LEFT JOIN `". RL_DBPREFIX ."banner_plans` AS `BT1` ON `T1`.`Plan_ID` = `BT1`.`ID` WHERE ";
$sql = str_replace(array('FROM', 'WHERE'), $replace, $sql);


global $data, $key, $lang, $config;

if ( version_compare($config['rl_version'], '4.0.1', '>') )
{
	foreach($data as $key => $transaction)
	{
		if ( $data[$key]['Service'] == 'banners' )
		{
			$planName = $data[$key]['Banner_plan'] ? $lang["banner_plans+name+{$data[$key]['Banner_plan']}"] : $lang['banners_transactionsPlanRemoved'];
			$item = $data[$key]['Item_ID'] ? "{$lang['banners_planType']} (#{$data[$key]['Item_ID']})" : $lang['item_not_available'];
			$data[$key]['Item'] = "{$item}|{$planName}";
		}
	}
}
else
{
	if ( $data[$key]['Service'] == 'banners' )
	{
		$planName = $data[$key]['Banner_plan'] ? $lang["banner_plans+name+{$data[$key]['Banner_plan']}"] : $lang['banners_transactionsPlanRemoved'];
		$item = $data[$key]['Item_ID'] ? "{$lang['banners_planType']} (#{$data[$key]['Item_ID']})" : $lang['item_not_available'];
		$data[$key]['Item'] = "{$item}|{$planName}";
	}
}


global $rlDb, $transactions, $key, $lang;

if ( $transactions[$key]['Service'] == 'banners' )
{
	$sql  = "SELECT `T1`.`ID`, `T2`.`Key` FROM `". RL_DBPREFIX ."banners` AS `T1` ";
	$sql .= "LEFT JOIN `". RL_DBPREFIX ."banner_plans` AS `T2` ON `T1`.`Plan_ID` = `T2`.`ID` ";
	$sql .= "WHERE `T1`.`ID` = '{$transactions[$key]['Item_ID']}' LIMIT 1";
	$bannerInfo = $rlDb -> getRow($sql);

	$planName = $lang["banner_plans+name+{$bannerInfo['Key']}"];
	$transactions[$key]['plan_info'] = $planName ? "{$planName} ({$lang['banners_planType']})" : false;
	$transactions[$key]['item_info'] = $lang["banners+name+{$bannerInfo['ID']}"];
}

