plugin.install:
global $rlDb;
$sql = " CREATE TABLE `".RL_DBPREFIX."rss_feed` ( `ID` int(11) NOT NULL auto_increment, `Key` varchar(50) NOT NULL default '', `Side` varchar(15) NOT NULL default '', `Tpl` enum('1','0') NOT NULL default '1', `Url` varchar(255) NOT NULL default '', `Date` datetime NOT NULL default '0000-00-00 00:00:00', `Article_num` int(2) NOT NULL default '5', `Status` enum('active','approval') NOT NULL default 'active', KEY `ID` (`ID`) )CHARSET=utf8";
$rlDb -> query($sql);
